// The page that users can use to upload an image, open to guests and users 
// Primary component: UploadPage
import React, { Component } from 'react'; 
import { Grommet, grommet, Box, Button, Heading, Menu, Paragraph, Text, Meter} from 'grommet';
import * as Icons from 'grommet-icons';
import { Switch, BrowserRouter as Router, browserHistory, Redirect, withRouter, Link  } from 'react-router-dom';

// IMPORT: Style components 
import Style from './styles/Style';
import Colors from './styles/Color';


// IMPORT: Action scripts 
import AuthService from './actions/AuthService';
import ScanService from './actions/ScanService'; 

// IMPORT: React Pages 
import Home from './Home'; 


const horizontalSpacer = (amt) => { 
	return( 
		<Box width="100%" height={amt}> 
		</Box> 
	); 
} 


class CircleMeter extends React.Component {
	state = { value: 20 };

	componentDidMount() {
	  this.timer = setInterval(() => {
		const { value } = this.state;
		this.setState({ value: value < 100 ? value + 8 : 20 });
	  }, 50);
	}

	componentWillUnmount() {
	  clearInterval(this.timer);
	}

	render() {
	  const { value } = this.state;
	  return (
			<Meter
			margin="0"
			alignSelf="start"
			height="25px"
			width="25px"
			type="circle"
			size="small"
			values={[{ value, color: value > 50 ? `${Colors.grey1}` : `${Colors.grey3}` }]}/>
	  );
	}
  }


class UploadPage extends Component { 

	constructor() {
		super();
		this.state = {}; 
		this.state.imgloaded = false; 
		this.state.imgurl = undefined; 
		this.state.idloaded = false; 
		this.productID = undefined;  
	} 

	onChangeHandler=event=>{
		let id = undefined; 
		let reader = new FileReader(); 
		console.log("ATTEMPTING TO GET ID");
		reader.readAsDataURL(event.target.files[0]); 
		reader.onload = (e) => {
			this.setState({ imgloaded: true });
			console.log("SENDING SCAN REQ");
			ScanService.getProductID(e.target.result)
			.then(res => { 
				this.setState({ idloaded: true}); 
				id = res.data.product_id;
				console.log("ID ACQUIRED: ", id);
				this.setState({ imgurl: e.target.result });
				this.setState({ fileData: e.target.result, productID: id}); 
			})
			.catch(err => { 
				console.log("ERROR OCCURED UPLOADING IMAGE", err);
			});
		} 
		this.setState({
			selectedFile: event.target.files[0],
		})	
	}


	// FILE UPLOAD BUTTON 
	uploadFile = () => { 
		return(
			<Box width="45%" background={Colors.primary}>  
				<input type='hidden' id='file' onChange={this.onChangeHandler} 
				style={{ 
    				visibility: 'hidden',
					width: '100%', 
					height: '100%', 
					cursor: 'pointer',
				}}
				/>
			</Box> 
		); 
	} 

	getProductID = () => {
		return this.state.productID;
	} 

	// SUBMIT BUTTON 
	submitButton = () => {
		let id = this.getProductID();
		ScanService.cacheProduct(id); 
		return( 
			<Link to={`product/${id}`} productID={id} style={{ textDecoration: 'none' }}> 
				<Text size="12pt" color={Colors.grey3}> Submit</Text>
			</Link> 
		); 
	} 

	// UPLOAD & SUBMIT CONTAINER 
	fileUploaded = () => {
		if(this.state.idloaded == true) {
			let pageid = this.getProductID(); 
			return(
				<Box direction="row" background={Colors.primary} pad="small" width="45%" justify="between" align="center">
					{this.submissionIcon()}
					{this.submitButton()} 
				</Box>  
			);
		} else if(this.state.imgloaded == true) { 
			return ( 
				<Box direction="row" background={Colors.primary} pad="small" width="45%" justify="between" align="center">
						{this.submissionIcon()}
					<Box background="red" />
					<Text size="12pt" color={Colors.grey3}> Loading</Text> 
				</Box> 
			)
	    } else { 
			return( 
				<Box direction="row" background={Colors.primary} pad="small" width="45%" justify="between" align="center">
					{this.submissionIcon()}
					<Text size="12pt" color={Colors.grey3}> Submit</Text> 
				</Box>  
			);

		}

	} 

	submissionIcon = () => { 
		if( this.state.idloaded == true) { 
			return( 
				<Icons.CheckboxSelected	size="medium" color={Colors.grey3}/>
			);
		} else if(this.state.imgloaded == true) { 
			return ( 
				<Box> 
					<CircleMeter />
				</Box>
			);
		} else { 
			return( 
				<Icons.Checkbox size="medium" color={Colors.grey3} />
			);
		}
	}

	imageDisplay = () => { 
		if(this.state.imgurl === undefined) {
			return( 
				<Box background={Colors.grey1} pad="10%">
					<Icons.Camera color={Colors.grey3} size="large"/>
				</Box> 
			);
		} else { 
			return(
				<img src={this.state.imgurl} height="310px" width="310px" alt="preview" />
			)
		}
	}

	// MAIN VIEW
	render() { 
		return(
			<Box margin="large" justify="center" align="center">
				<Box justify="between" width="100%" direction="row"> 
					<Box width="33%"  justify="center" align="start"   pad={{ left: "large" }}>
						<Link to='/home'>
							{<Icons.LinkPrevious color={Colors.dark3} size="large" justify="left" align="left"/>}
						</Link>
					</Box> 
					<Box width="33%" justify="center" align="center"> 
						{Style.Title("Upload an image")}
					</Box> 
					<Box width="33%"> 
					</Box> 
				</Box> 
				{horizontalSpacer("10px")} 
				<div> 
					<Text size="12pt" color={Colors.grey1}> Not sure how it works? You can find a guide </Text> 
					<Text size="12pt" color={Colors.primary}> here </Text>
				</div> 
				{horizontalSpacer("20px")} 
				{this.imageDisplay()}
				{horizontalSpacer("20px")}
				<Box direction="row" justify="start" width="23.5%"> 
					{this.uploadFile()}
					<Box width="10%"/>
					{this.fileUploaded()}
				</Box> 
			</Box>
		);	
	}
}


export default withRouter(UploadPage);
