import React, { Component } from 'react'; 
import { Grommet, grommet, Box, Button, Heading, Menu, Paragraph, Text } from 'grommet';
import * as Icons from 'grommet-icons';
import { Switch, BrowserRouter as Router, browserHistory, Redirect, withRouter, Link  } from 'react-router-dom';
import Colors from './styles/Color';
import Style from './styles/Style';
import Home from './Home'; 
import AuthService from './actions/AuthService';

class ProfilePage extends Component { 

	constructor(props) {
		super(props); 
		this.state = {}; 
		this.state.userdata = JSON.parse(localStorage.getItem('userdata'));
	} 

	// MAIN VIEW
	render() { 
		return(
			<Box direction="column" gap="large" margin="large" justify="center" align="center">
				{Style.Title(`Welcome back ${this.state.userdata.username}`)}
				<Box style={{ borderRadius: '50%' }}  align="center" pad="medium" 
				border={{ color: `${Colors.dark3}`, size: "10px", side: "all"}}
				justify="center">
					<Icons.Configure color={Colors.dark3} size="xlarge" />
				</Box>
			</Box>
		);	
	}
}


export default withRouter(ProfilePage);
