import React, { Component } from 'react'; 
import { Grommet, grommet, Box, Heading, Menu } from 'grommet';
import * as Icons from 'grommet-icons';
import { Redirect, BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import AuthService from './actions/AuthService';

import Home from './Home'; 
import UploadPage from './Upload'; 
import ProductPage from './Product';
import Navbar from './Navbar';
import NoPage from './404.js';
import homebg from './styles/homebg.png'; 
import Colors from './styles/Color.js';
import ProfilePage from './Profile';

// THE VIEW RENDERING OBJECT 
class View extends Component { 

	componentDidMount() { 
		this.setState({ loaded: true }); 
		this.routeChanged()
	} 

	getbg = () => {
		if(this.getLocationHome() === true) { 
			return "url("+homebg+")";
		} else { 
			return Colors.grey3;
		}
	}

	componentDidUpdate (prevProps) {
		let { location: { pathname } } = this.props

		if (prevProps.location.pathname === pathname) return
		this.routeChanged()
	}
	routeChanged () {
		let { location, push, replace, actions } = this.props
	}
	getLocationHome = () => {
		let path = this.props.location.pathname; 
		if(path == "/" || "/home") { 
			return true; 
		} else { 
			return false; 
		}
	} 

	render() {
		let productid = "0"; 
		return(
			<Router>
				<Switch>
				<Box alignContent='between' fill={true} > 
					<Box flex={true} fill={true} direction="row">
						<Box direction="row" fill={true} overflow="scroll">
							<Box pad={{ top: 'xsmall' }} fill={true} background={this.getbg()}> 
									<Navbar />
									<Route exact path="/" component={Home} />	
									<Route path='/home' component={Home} /> 
									<Route path='/about' component={NoPage} /> 
									<Route path='/about/team' component={NoPage} />
									<Route path='/upload' component={UploadPage} />
									<Route path={`/product/:productId`} component={ProductPage} />
									<Route path={`/profile/:profileId`} component={ProfilePage} />
									<Route path='/404' component={NoPage} /> 
							</Box>
						</Box>
					</Box>
				</Box>
				</Switch> 
			</Router> 
		);	
	}
} 




export default View;

