import React, { Component } from 'react';
import { Grommet, DataTable, grommet, Meter, TextInput, DropButton, Button, Box, Heading, Menu, Paragraph, Carousel, Accordion, AccordionPanel, Text, Image, Distribution, Layer } from 'grommet';
import * as Icons from 'grommet-icons';
import { withRouter, Redirect, Route, Link, Router, BrowserRouter } from 'react-router-dom';

import Style from './styles/Style';
import Colors from './styles/Color';
import AuthService from './actions/AuthService';
import UserService from './actions/UserService';

const testdata = {
	"TEMPORARY": [
		{
			name: "TEST INGREDIENT 1",
			category: "TEMPORARY",
			description: "This is a test!",
			imgurl: "https://gist.githubusercontent.com/Michael-Lloyd/3dc80552636238b608be78cfc352ceb0/raw/539a312a61fb061c9c79888b16cf10543a74ee35/image",
			warning: {
				msg: "!",
				color: "status-error"
			},
			highlighting: { 
				msg: "I don't want this",
				color: 1, 
			}
		}, 
		{
			name: "TEST INGREDIENT 2",
			category: "TEMPORARY",
			description: "This is a test!",
			imgurl: "https://gist.githubusercontent.com/Michael-Lloyd/3dc80552636238b608be78cfc352ceb0/raw/539a312a61fb061c9c79888b16cf10543a74ee35/image",
			warning: {
				msg: "!",
				color: "status-error"
			},
			highlighting: { 
				msg: "I don't want this",
				color: 2, 
			}
		}
	],
	"TEMPORARY 2": [ 
		{
			name: "TEST INGREDIENT 3",
			category: "TEMPORARY 2",
			description: "This is a test!",
			imgurl: "https://gist.githubusercontent.com/Michael-Lloyd/3dc80552636238b608be78cfc352ceb0/raw/539a312a61fb061c9c79888b16cf10543a74ee35/image",
			warning: {
				msg: undefined,
				color: undefined,
			},
			highlighting: { 
				msg: undefined, 
				color: undefined,
			}
		},
		{
			name: "TEST INGREDIENT 4",
			category: "TEMPORARY 2",
			description: "This is a test!",
			imgurl: "https://gist.githubusercontent.com/Michael-Lloyd/3dc80552636238b608be78cfc352ceb0/raw/539a312a61fb061c9c79888b16cf10543a74ee35/image",
			warning: {
				msg: undefined,
				color: undefined,
			},
			highlighting: { 
				msg: undefined, 
				color: undefined,
			}
		}
	]
}

const teststats = { 
	total_ingredients: 4, 
	total_highlighting: { 
		"1": 1, 
		"2": 1, 
	},
	category_stats: { 
		"TEMPORARY": { 
			total: 2, 
			highlighting: { 
				"1": 1, 
				"2": 1,
			},
			percentage: 50,
		}, 
		"TEMPORARY 2": { 
			total: 2, 
			highlighting: undefined,
			percentage: 50,
		}
	}
}


// ---------------------------------// ---------------------------------// ---------------------------------
// ---------------------------------// ---------------------------------// ---------------------------------

class ProductPage extends Component {

	constructor(props) {
		super(props);
		this.state = {};

		// If page has acquired everything 
		this.state.loading = true;
		// If loading resulted in an error 
		this.state.loaderr = undefined;
		// The ID of this product page 
		this.state.product_id = this.props.match.params.productId;
		// An object of ingredient ID's by category 
		this.state.ingredients = undefined;
		// Collated and categorized ingredient information
		this.state.data = undefined;
		// A collection of useful statistics to display
		this.state.statistics = undefined;
		// A check to see if the user is authenticated 
		this.state.loggedin = localStorage.getItem('auth');
		// If the user is logged in, get their userdata 
		this.state.userdata = JSON.parse(localStorage.getItem('userdata'));
		// Check if this productID has been saved before (by the user)
		this.state.saved = undefined;
	}

	componentDidMount() {
		let fetch_data = setInterval(() => {
			// TODO: REPALCE WITH ACTUAL SERVER CALL
			this.setState({ ingredients: [1] });
			this.setState({ data: testdata, statistics: teststats, loading: false });
			clearInterval(fetch_data);
		}, 1000);

	}

	loadingScreen = () => {
		if (this.state.loaderr !== undefined) {
			return (
				<Box align="center" gap="small" fill >
					<Box height="20%" />
					<Box align="center" pad="medium" gap="medium" border={{ color: "status-error", size: "medium" }}>
						<Icons.Alert size="xlarge" color="status-error" />
						<Text color="status-error">Failure loading: {this.state.product_id} <br /> <br />
							<Link to='/upload' style={{ textDecoration: "none" }}>
								<Box direction="row">
									<Icons.LinkPrevious color="status-error" size="medium" />
									<Box width="10px" />
									<Text color="status-error">Go back</Text>
								</Box>
							</Link>
						</Text>
					</Box>
				</Box>
			)
		} else {
			return (
				<Box align="center" gap="large" fill>
					<Box height="25%" />
					<PreviewLoader />
					<Text color={Colors.grey1}>Loading page: {this.state.product_id}</Text>
				</Box>
			)
		}
	}

	resultPage = () => { 
		let statedata = this.state;
		return( 
			<Box gap="medium" margin="large" justify="start" align="center" fill>
				{headerElements()}
				<Box height="50px" />
				<Text size="24pt">Overview of contents:</Text> 
				<Text size="12pt"> We found the following substances in the label you sent us: </Text> 
				{breakdownTable(statedata)}
				<Text size="24pt">Ingredient appearances:</Text> 
			</Box>
		)
	}

	render() {
		let { loading } = this.state;
		return (
			<Box width="100%">
				{loading && this.loadingScreen()}
				{!(loading) && this.resultPage()}
			</Box>
		)
	}

}


// ---------------------------------// ---------------------------------// ---------------------------------
// ---------------------------------// ---------------------------------// ---------------------------------

class PreviewLoader extends React.Component {
	state = { value: 10 };

	componentDidMount() {
		this.timer = setInterval(() => {
			const { value } = this.state;
			this.setState({ value: value < 100 ? value + 8 : 0 });
		}, 50);
	}

	componentWillUnmount() {
		clearInterval(this.timer);
	}

	render() {
		const { value } = this.state;
		return (
			<Meter
				margin="0"
				height="100px"
				width="100px"
				type="circle"
				size="small"
				values={[{ value, color: value > 10 ? `${Colors.primary}` : `${Colors.primary}` }]} />
		);
	}
}

const breakdownTable = (props) => { 
	let columns = [
		{
			property: "category",
			header: <Text>Ingredient Category</Text>,
			primary: true,
			footer: "Total"
		},
		{
			property: "amount",
			header: "Amount"
		},
		{
			property: "percent",
			header: "Percent Complete",
			render: datum => (
				<Box pad={{ vertical: "xsmall" }}>
					<Meter
						values={[{ value: datum.percent }]}
						thickness="medium"
						size="xsmall"
					/>
				</Box>
			)
		}
	];
	let data = [ 
		{ category: "A", amount: "5", percent: 5}
	]; 
	return( 
		<Box width="800px">
			<DataTable columns={columns} data={data} />
		</Box>
	)
}

const headerElements = () => {
	return (
		<Box justify="between" width="100%" direction="row">
			<Box width="33%" justify="center" align="start" pad={{ left: "large" }} background="red">
				<Link to='/upload'>
					{<Icons.LinkPrevious color={Colors.dark3} size="large" justify="left" align="left" />}
				</Link>
			</Box>
			<Box width="33%" justify="center" align="center" background="red">
				{Style.Title("Scan Results:")}
			</Box>
			<Box gap="small" width="33%" justify="start" align="center" background="red">
				<Icons.CircleQuestion color={Colors.dark3} size="large" justify="left" align="right" />
				<Icons.LinkBottom color={Colors.dark3} size="large" justify="left" align="right" />
			</Box>
		</Box>
	);
}

export default withRouter(ProductPage);
