import React, { Component } from 'react'; 
import { Grommet, grommet, Box, Heading, Menu } from 'grommet';
import * as Icons from 'grommet-icons';
import { Switch, Route, Link } from 'react-router-dom'; 

// PAGES IMPORTED 
import Home from './pages/Home'; 
//import Help from './Help';
//import UploadPage from './Upload'; 
//import Product from './Product'; 
//import Login from './Login'; 
import Navbar from './Navbar';
import Background from './Background'; 
//import Footer from './Footer';  
//import Errors from './Error'; 
//import FourOFour from './404.js';
//import User from './User'; 
//import UserProduct from './UserProduct';
//import Registration from './Register';
//import compare from './compare';
//import savedProduct from './savedProduct';
//import personalSetting from './personalSetting';
//import compareResult from './compareResult';
//import highlightPage from './highlightPage';

// THE VIEW RENDERING OBJECT 
class View extends Component { 
	render() { 
		return(
					<div> 
						<Background /> 
						<Box direction="column" justify="start" height="100%" alignContent='start' fill={true}>
							<Navbar />
							<Box flex={true} fill={true} direction="row">
								<Box direction="row" fill={true} flex={true} overflow="scroll">
									<Switch>
										<Route exact path="/" component={Home}  />	
										<Route path='/home' component={Home}  /> 
									</Switch> 
								</Box>
							</Box>
						</Box>
					</div> 
		);	
	}
} 

export default View;

