import axios from 'axios'; 

const backendurl = 'https://0d569b81-281f-42aa-b7c8-982eef0cfaa0.mock.pstmn.io/';

const getProductPage = id => { 
    let pid = String(id);
    let url = `${backendurl}/product?=${pid}`;
    return( 
        axios
        .get(url)
    )
}

const getProductID = b64_data => {
    return ( 
        axios.post(`${backendurl}/upload`, String(b64_data))
    )
}

const cacheProduct = (id) => { 
    let pagePromise = getProductPage(id); 
    pagePromise.then( res => { 
        localStorage.setItem(id, res.data);
    })
}

export default { 
    getProductID, 
}
