//
import Colors from './Color.js';
import * as Grommet from "grommet";
import * as Icons from "grommet-icons";

const Title = (input) => { 
    return( 
		<Text size="36pt" color={Colors.dark3}> 
			{input} 
		</Text> 
    );
};

const SubTitle = (input) => { 
    let ptsize = "12pt";
    return( 
		<Text size={ptsize} color={Colors.grey1}> 
			{contents}
		</Text> 
    );
};

const Hint = (hint) => { 
    let ptsize = "12pt"; 
    let ptcolor = Colors.grey2; 
    return( 
        <Text size={ptsize} color={ptcolor}>
            {hint}
        </Text> 
    );
};

const Body = (input) => { 
    let ptsize = "12pt"; 
    let ptcolor = Colors.dark3; 
    return( 
        <Text size={ptsize} color={ptcolor}> 
            {input}
        </Text> 
    );
};

const BodyColored = (input, color) => { 
    let ptsize = "12pt"; 
    let ptcolor = color; 
    return(
        <Text size={ptsize} color={ptcolor}>
            {input}
        </Text> 
    );
};

export default { 
    Title, 
    SubTitle, 
    Hint, 
    Body, 
    BodyColored 
}
