import React, { Component } from 'react'; 
import { Grommet, grommet, Meter, TextInput, DropButton, Button, Box, Heading, Menu, Paragraph, Carousel, Accordion, AccordionPanel, Text, Image, Distribution, Layer } from 'grommet';
import * as Icons from 'grommet-icons';
import { withRouter, Redirect, Route, Link, Router, BrowserRouter } from 'react-router-dom';

import Style from './styles/Style';
import Colors from './styles/Color'; 
import AuthService from './actions/AuthService'; 
import UserService from './actions/UserService';


class ProductPage extends Component { 

	constructor(props) { 
		super(props); 
		this.state = {}; 

		// If page has acquired everything 
		this.state.loading = true; 
		// If loading resulted in an error 
		this.state.loaderr = undefined; 
		// The ID of this product page 
		this.state.product_id = this.props.match.params.productId; 
		// An object of ingredient ID's by category 
		this.state.ingredients = undefined; 
		// A collection of useful statistics to display
		this.state.statistics = undefined; 
		// A check to see if the user is authenticated 
		this.state.loggedin = JSON.parse(localStorage.getItem('auth')); 
		// If the user is logged in, get their userdata 
		this.state.userdata = JSON.parse(localStorage.getItem('userdata'));
		// Check if this productID has been saved before (by the user)
		this.state.saved = undefined; 
	}

	loadingScreen = () => { 
		return( 
			<Box>
				<PreviewLoader />
				{this.state.product_id}
			</Box>
		)
	}

	render() { 
		return ( 
			<Box> 

				{ this.state.loading && this.loadingScreen()}
			</Box> 
		)
	}

}


// ---------------------------------// ---------------------------------// ---------------------------------
// ---------------------------------// ---------------------------------// ---------------------------------

class PreviewLoader extends React.Component {
	state = { value: 10 };

	componentDidMount() {
	  this.timer = setInterval(() => {
		const { value } = this.state;
		this.setState({ value: value < 100 ? value + 8 : 1 });
	  }, 50);
	}

	componentWillUnmount() {
	  clearInterval(this.timer);
	}

	render() {
	  const { value } = this.state;
	  return (
			<Meter
			margin="0"
			height="100px"
			width="100px"
			type="circle"
			size="small"
			values={[{ value, color: value > 10 ? `${Colors.primary}` : `${Colors.grey1}` }]}/>
	  );
	}
  }

export default withRouter(ProductPage);
