	); 
} 

const horizontalSpacer = (amt) => { 
	return( 
		<Box width="100%" height={amt}> 
		</Box> 
	); 
} 

// TODO: 
class submitButton extends Component { 

} 

const linkText = (content, size, path, color) => { 
	return( 
		<Link to={path}>
			<Text size={size} color={color}> {content} </Text> 
		</Link> 
	); 
} 

class UploadPage extends Component { 

	render() { 
		return(
			<Box margin="large" align="center" justify="center"> 
				{uploadTitle("Upload an image")}
				{horizontalSpacer("10px")} 
				{subTitle("Note sure how it works? you can find a guide ", 12)}
				{linkText("here.", 12, "/help", Colors.primary)}
			</Box>  
		);	
	}
} 

export default UploadPage;
